policies.inbound:










			http://samplessoap.azurewebsites.net/UserSession.asmx
			POST

				http://tempuri.org/Session


				text/xml

			@(context.Request.Body.As(preserveContent: true))

		<set-variable name="SessionToken" value="@{
                                                        XNamespace soapNS = "http://schemas.xmlsoap.org/soap/envelope/";
                                                        XNamespace responseNS = "http://tempuri.org/";

                                                        var element = ((IResponse)context.Variables["SessionTokenSOAPResponse"]).Body.As(preserveContent: true)
                                                                            .Elements(soapNS + "Envelope")
                                                                            .Elements(soapNS + "Body")
                                                                            .Elements(responseNS + "SessionResponse")
                                                                            .Elements(responseNS + "SessionResult")
                                                                            .FirstOrDefault();

                                                        return element.Value;
                                                    }" />



			http://tempuri.org/Send


			text/xml





						{{context.Variables['SessionToken']}}
